import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Admin.css';

const Admin = () => {
  const navigate = useNavigate();
  const [assignments, setAssignments] = useState({
    AboutMe: null,
    Address: null,
    BirthDate: null
  });

  const pages = ['page1', 'page2'];

  // Initialize with default assignments
  useEffect(() => {
    const savedAssignments = localStorage.getItem('componentAssignments');
    if (savedAssignments) {
      const parsed = JSON.parse(savedAssignments);
      const inverted = {};

      // Convert from page->components to component->page format
      Object.entries(parsed).forEach(([page, components]) => {
        components.forEach(comp => {
          inverted[comp] = page;
        });
      });

      setAssignments(inverted);
    } else {
      // Default assignments
      setAssignments({
        AboutMe: 'page1',
        Address: 'page1',
        BirthDate: 'page2'
      });
    }
  }, []);

  const handleAssignmentChange = (component, newPage) => {
    setAssignments(prev => {
      // If component is being unassigned from its current page
      if (newPage === prev[component]) {
        // Find another component to assign to this page to maintain at least one
        const otherComponents = Object.keys(prev).filter(c => c !== component);
        const otherAssigned = otherComponents.find(c => prev[c] === newPage);

        if (!otherAssigned) {
          // Can't unassign - must have at least one component per page
          return prev;
        }

        return { ...prev, [component]: null };
      }

      // If component is being assigned to a new page
      return { ...prev, [component]: newPage };
    });
  };

  const handleSave = () => {
    // Convert back to page->components format
    const pageAssignments = { page1: [], page2: [] };

    Object.entries(assignments).forEach(([component, page]) => {
      if (page) {
        pageAssignments[page].push(component);
      }
    });

    // Ensure each page has at least one component
    if (pageAssignments.page1.length === 0 || pageAssignments.page2.length === 0) {
      alert('Each page must have at least one component!');
      return;
    }

    localStorage.setItem('componentAssignments', JSON.stringify(pageAssignments));
    alert('Assignments saved successfully!');
    navigate('/');
  };

  // Convert assignments to page->components for display
  const getComponentsForPage = (page) => {
    return Object.entries(assignments)
      .filter(([, assignedPage]) => assignedPage === page)
      .map(([component]) => component);
  };

  return (
    <div className="admin-container">
      <h1>Component Management</h1>
      <p>Assign components to pages (each component can only be on one page)</p>

      <div className="pages-container">
        {pages.map(page => (
          <div key={page} className="page-column">
            <h2>{page.toUpperCase()}</h2>
            <div className="component-list">
              {getComponentsForPage(page).map(component => (
                <div key={component} className="component-item">
                  {component}
                  <button 
                    onClick={() => handleAssignmentChange(component, null)}
                    className="remove-button"
                  >
                    Ã—
                  </button>
                </div>
              ))}
            </div>

            <div className="available-components">
              <h3>Available Components</h3>
              {Object.keys(assignments)
                .filter(comp => !assignments[comp] || assignments[comp] === page)
                .map(component => (
                  <div 
                    key={component}
                    className={`available-item ${assignments[component] === page ? 'assigned' : ''}`}
                    onClick={() => handleAssignmentChange(
                      component, 
                      assignments[component] === page ? null : page
                    )}
                  >
                    {component}
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>

      <button onClick={handleSave} className="save-button">
        Save Assignments
      </button>
    </div>
  );
};

export default Admin;
